const axios = require("axios");
const cheerio = require("cheerio");
const header = {
  'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3;q=0.7',
  'User-Agent': 'Mozilla/5.0 (Linux; Android 10; K) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/116.0.0.0 Mobile Safari/537.36'
}
function splitToNChunks(array, n) {
  let result = [];
  for (let i = n; i > 0; i--) {
    result.push(array.splice(0, Math.ceil(array.length / i)));
  }
  return result;
}


const lastUpdate = async (page = 1) => {
  try {
    if (page == "0") return `Page Doesn't Exist!!`
    const url = `https://asuracomics.com/page/${page}/`;
    const res = await axios.get(url, { header })
    const $ = cheerio.load(res.data)

    const data = [];
    let img = [];
    let title = [];
    let links = [];


    $('.imgu img').each((i, el) => {
      let image = $(el).attr('src')
      if (image !== '') {
        img.push(image)
      }
    })


    $('.series h4').each((i, el) => {
      let titles = $(el).text()
      title.push(titles)
    })


    let chap = $('.luf a').text().trim().split("\n")
    let chapter = splitToNChunks([...chap], 50)

    $('.luf a.series').each((i, el) => {
      let link = $(el).attr('href').split("\n")
      links.push(...link)
    })



    for (let i = 0; i < img.length; i++) {
      let chapters = chapter[i][1].split("Chapter ").slice(1)
      let datas = {
        image: img[i],
        title: title[i],
        link: links[i],
        episodes: chapters
      }
      data.push(datas)
    }
    return data;
  } catch (err) {
    return `An Error Occured!!`
  }
}

module.exports = lastUpdate