const axios = require("axios");
const cheerio = require("cheerio");
const header = {
  'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3;q=0.7',
  'User-Agent': 'Mozilla/5.0 (Linux; Android 10; K) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/116.0.0.0 Mobile Safari/537.36'
}

const epDownloader = async (url) => {
  try {
    if (!url) return `Give the Url Of the Episode!!`
    const res = await axios.get(url, { header })
    const $ = cheerio.load(res.data)
    let image = []
    $('.rdminimal p img').each((i, el) => {
      const img = $(el).attr('src')
      image.push(img)
    })
    let ima ;
    let str = JSON.stringify(image)
    if(str.includes("ENDING-PAGE.jpg")||str.includes('ending-page')) {
      ima = image.slice(1,-1)
    } else {
      ima = image.slice(1)
    }
    return ima;
  } catch (err) {
    return ("An Error Occurred!!", err)
  }
}

module.exports = epDownloader
