const axios = require("axios");
const cheerio = require("cheerio");
const header = {
  'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/avif,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3;q=0.7',
  'User-Agent': 'Mozilla/5.0 (Linux; Android 10; K) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/116.0.0.0 Mobile Safari/537.36'
}

const search = async (text = 'Dr. player', page = '1') => {
  const url = `https://asuratoon.com/page/${page}/?s=${text}`;
  const res = await axios.get(url, { header })
  const $ = cheerio.load(res.data)
  let data = [];

 let images =  $('.bsx img')
   .map((i, el) => $(el).attr('src'))
  if (images.length === 0) return `No Data Found!!`

  let ids = $('.bsx a')
    .map((i, el) => {
      return $(el).attr('href')
.replace('https://asuratoon.com/manga/', '')
    })

 let titles = $('.bsx a').map((i, el) => $(el).attr('title'))

 let last_ep = $('.epxs')
    .map((i, el) => $(el).text())

 let ratings = $('.numscore')
   .map((i, el) => $(el).text())

  for (let i = 0; i < images.length; i++) {
    let datas = {
      id:ids[i],
      image: images[i],
      title: titles[i],
      rating: ratings[i],
      last_ep: last_ep[i]
    }
    data.push(datas)
  }
  return data;
}

module.exports = search